remarks.code:

// Subscribe.
this.propertyChangedListener = new WeakEventListener<RadGaugeBase, AnimationSettings, PropertyChangedEventArgs>(this, this.animationSettings);
this.propertyChangedListener.OnEventAction = (listener, sender, eventArgs) => 
{
    // Do not use 'this' here, only use the arguments.
    listener.AnimationSettings_PropertyChanged(sender, eventArgs);
};
this.propertyChangedListener.OnDetachAction = (weakEventListener, listener, eventSource) =>
{
    // Do not use 'this' here, only use the arguments. Also note that listener and/or eventSource may be null here.
    if (eventSource != null)
    {
        eventSource.PropertyChanged -= weakEventListener.OnEvent;
    }
};
this.animationSettings.PropertyChanged += this.propertyChangedListener.OnEvent;

// Unsubscribe.
if (this.propertyChangedListener != null)
{
    this.propertyChangedListener.Detach();
    this.propertyChangedListener = null;
}
